import React, { useState } from "react";
import { connect } from "react-redux";
import { Modal, Button } from "react-bootstrap";
import { addDataProduct } from "../../actionCreators/AdminAction";

const AdminProductAdd = (props) => {
  const [imageLink, setimageLink] = useState("");

  const [dataAddInput, setDataAddInput] = useState({
    image: null,
    name: "",
    price: "",
    description: "",
    quantity: "",
    productType: "Eyes",
  });

  const handleAddInputChange = (event) => {
    setDataAddInput({
      ...dataAddInput,
      [event.currentTarget.name]: event.currentTarget.value,
    });
  };

  const setImageLink = (event) => {
    // Assuming you are now storing the image URL as a string
    setDataAddInput({
      ...dataAddInput,
      image: event.currentTarget.value,
    });
    setimageLink(event.currentTarget.value);
  };

  const closeAddModal = () => {
    props.unDisplayAddModal(false);
  };

  //  Form Edit Data = for image inputs, we use form data in Insomnia. So here we are.
  const FormAddData = new FormData();
  FormAddData.append("image", dataAddInput.image);
  FormAddData.append("name", dataAddInput.name);
  FormAddData.append("price", dataAddInput.price);
  FormAddData.append("description", dataAddInput.description);
  FormAddData.append("quantity", dataAddInput.quantity);
  FormAddData.append("productType", dataAddInput.productType);

  const handleSubmitAdd = (event) => {
    event.preventDefault();
    props.addDataProduct(FormAddData, dataAddInput);
    setDataAddInput({
      image: null,
      name: "",
      price: "",
      description: "",
      quantity: "",
      productType: "tops",
    });
    props.unDisplayAddModal(false);
  };

  return (
    <Modal show={props.showAddModal} onHide={closeAddModal}>
      <form onSubmit={handleSubmitAdd}>
        <Modal.Header closeButton>
          <Modal.Title>Add Product</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <div>
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                className="form-control"
                placeholder="Name"
                name="name"
                onChange={handleAddInputChange}
              />
            </div>
            <div className="d-flex d-row">
              <div className="form-group">
                <label htmlFor="price">Price</label>
                <input
                  type="number"
                  className="form-control"
                  placeholder="Price"
                  name="price"
                  onChange={handleAddInputChange}
                />
              </div>
              {/* Stock(Frontend) = Quantity(Backend) */}
              <div className="form-group ml-3">
                <label htmlFor="quantity">Stock</label>
                <input
                  type="number"
                  className="form-control"
                  placeholder="Stock"
                  name="quantity"
                  onChange={handleAddInputChange}
                />
              </div>
            </div>
            <div className="form-group">
              <label>Link Image</label>
              <div className="custom-file">
                <input
                  name="image"
                  type="text"
                  accept="image/*"
                  className="custom-file-input"
                  id="customFile"
                  onChange={selectFile}
                  style = {{width : "100%" }}
                />
                <label className="custom-file-label" htmlFor="customFile">
                  {imageLink ? (
                    <p className="text-success-s2 my-0">{imageLink}</p>
                  ) : (
                    ""
                  )}
                </label>
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                rows="3"
                type="text"
                className="form-control"
                placeholder="Description"
                name="description"
                onChange={handleAddInputChange}
              />
            </div>
            <div className="d-flex d-row">
              <div className="form-group ml-3 w-100">
                <label htmlFor="product-type">Select Product Type</label>
                <select
                  className="form-control"
                  name="productType"
                  onChange={handleAddInputChange}
                >
                  <option value="tops">Face</option>
                  <option value="bottom">Eyes</option>
                  <option value="outer wear">Lips</option>
                  <option value="outer wear">Accessories</option>
                  <option value="outer wear">Shaving Needs</option>

                </select>
              </div>
            </div>
          </div>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={closeAddModal}>
            Close
          </Button>
          <Button type="submit" variant="success">
            Add
          </Button>
        </Modal.Footer>
      </form>
    </Modal>
  );
};

const mapDispatchToProps = {
  addDataProduct,
};

export default connect(null, mapDispatchToProps)(AdminProductAdd);
